    def on_accept(self):
        last_sesion = ControlModule.getLastSession()
        id = last_sesion['Id_sesion']
        efefctivo_al_abrir_caja = int(last_sesion['Apertura_caja'])
        if self.lineEdit.text() != "" and int(self.lineEdit.text()) <= efefctivo_al_abrir_caja:
            if self.plainTextEdit.toPlainText() != "":
                monto = int(self.lineEdit.text())
                comentario = self.plainTextEdit.toPlainText()
                values = [monto, comentario, id]
                ControlModule.set_retiro(values)
                self.Dialog.accept()
            else:
                QMessageBox.warning(None, "Error!",
                                    "Debe ingresar al menos un comentario al retiro.")
        else:
            QMessageBox.warning(None, "Error!",
                                "Debe ingresar un monto a ser retirado menor o igual a la cantidad de dinero en "
                                "efectivo en la caja en el momento de su apertura.")
