    def on_index_change(self):
        nombre = self.listWidget.currentItem().text()
        data = ControlModule.get_User_by_name(nombre)
        if data:
            self.lineEdit_nombre.setText(nombre)
            self.lineEdit_contrasena.setText(data[0]['Contrasena'])
            if data[0]['Administra'] == 1:
                self.checkBox.setChecked(True)
            else:
                self.checkBox.setChecked(False)

    def on_accept(self):
        if self.listWidget.count() > 0:
            nombre = self.lineEdit_nombre.text()
            if self.checkBox.isChecked():
                admin = 1
            else:
                admin = 0
            data = [self.lineEdit_contrasena.text(), admin, nombre]
            rec = ControlModule.Update_User(data)
            if rec:
                QMessageBox.warning(None, "Error!", "Usuario no pudo ser actualizado, contacte al servicio técnico.")
        self.Dialog.accept()

    def get_all_names(self):
        names = ControlModule.Get_all_Usernames()
        self.listWidget.addItems(names)
